function [ListNodes] = GrowthFactor(Inputs, ListNodes, Solutions)% This function finds the best chain of veins used by the agents and% increases the radius of these vains by a pre-specified factor%% Inputs:% * Inputs      : Structure containing the PhysarumSolver inputs% * ListNodes       : Structure containing the graph% * Agents      : Structure containing the Agents%% Outputs: % * ListNodes       : Structure containing the updated graph%% Author: Aram Vroom - 2016% Email:  aram.vroom@strath.ac.uk%!!Atira Specific:!!%Normalize cost for # of nodes & for dVfor i = 1:length(Solutions.Nodes)    numberofnodes(i) = length(Solutions.Nodes{i});    costs(i) = sum(Solutions.Costs{i});endsumNodes = sum(numberofnodes);totalsumCosts = sum(costs);numberofnodecost = (sumNodes./numberofnodes)./sum(sumNodes./numberofnodes);solutioncosts = costs./totalsumCosts;totalcost = 0.5*numberofnodecost+0.5*solutioncosts;%Non-Atira specific% for i = 1:length(Solutions.Nodes)% totalcost(i) = sum(Solutions.Costs{i});% end%Find the minimum cost[~, mincostindex] = min(totalcost);%Determine which chain is the bestbestchain = Solutions.Nodes{mincostindex};%Loop over the nodes in this chainfor i = 1:length(bestchain)        %For ease of reading, define the node currently being evaluated & its parent    %as a separate variable    evaluatednode = char(bestchain(i));    parent = ListNodes.(evaluatednode).parent;        %Check if the node has a parent - Ignores root    if ~isempty(parent)                %Dilate the respective link in the parent's node structure        ListNodes.(evaluatednode).radius = ListNodes.(evaluatednode).radius + Inputs.GrowthFactor*ListNodes.(evaluatednode).radius;               %Check if the link's radius is not too large or small. Correct if        %so, set to min/max radius        ListNodes.(evaluatednode).radius(ListNodes.(evaluatednode).radius./Inputs.StartingRadius > Inputs.MaximumRadiusRatio) = Inputs.MaximumRadiusRatio*Inputs.StartingRadius;        ListNodes.(evaluatednode).radius(ListNodes.(evaluatednode).radius./Inputs.StartingRadius < Inputs.MinimumRadiusRatio) = Inputs.MinimumRadiusRatio*Inputs.StartingRadius;    endend